def transform(_input):
    def _fmt_temp(v):
        try:
            if v is None or isinstance(v, bool):
                return "NA"
            fv = float(v)
            s = ("{:.3f}".format(fv)).rstrip("0").rstrip(".")
            return s if s else "0"
        except Exception:
            s = str(v or "").strip()
            return s if s else "NA"

    def _fmt_seed(v):
        try:
            if v is None or isinstance(v, bool):
                return "-1"
            return str(int(float(v)))
        except Exception:
            s = str(v or "").strip()
            return s if s else "-1"

    prompt_id = str(_input.get("prompt_id") or "").strip() or "pXX"
    raw_repeat = _input.get("repeat")
    try:
        repeat_i = int(float(raw_repeat))
    except Exception:
        repeat_i = 0

    temp_s = _fmt_temp(_input.get("temperature"))
    seed_s = _fmt_seed(_input.get("seed"))
    run_key = "t" + temp_s + "_s" + seed_s

    case_dir = "cases/" + prompt_id + "/" + run_key + "/r" + str(repeat_i) + "/codeact"
    case_id = "codeact-" + prompt_id + "-" + run_key + "-r" + str(repeat_i)
    return {"case_dir": case_dir, "case_id": case_id}

