def transform(_input):
    fa = str(_input.get("final_answer") or "").strip()
    if fa:
        return fa

    steps = _input.get("steps")
    if isinstance(steps, list):
        for s_any in reversed(steps):
            s = s_any if isinstance(s_any, dict) else None
            if s is None:
                continue
            v = s.get("assistant_response")
            if v is None:
                v = s.get("response")
            txt = str(v or "").strip()
            if txt:
                return txt

    return ""

def transform(_input):
    def _as_int(x):
        try:
            if x is None:
                return None
            if isinstance(x, bool):
                return int(x)
            return int(float(x))
        except Exception:
            return None

    def _as_list(v):
        return v if isinstance(v, list) else []

    def _as_dict(v):
        return v if isinstance(v, dict) else {}

    mode = str(_input.get("mode") or "codeact")
    prompt_id = str(_input.get("prompt_id") or "")
    prompt_label = str(_input.get("prompt_label") or "")

    raw_repeat = _input.get("repeat")
    try:
        repeat_i = int(float(raw_repeat))
    except Exception:
        repeat_i = 0

    case_dir = str(_input.get("case_dir") or "")
    case_id = str(_input.get("case_id") or "")
    index_path = str(_input.get("index_path") or "")

    provider = str(_input.get("provider") or "")
    model = str(_input.get("model") or "")
    temperature = _input.get("temperature")
    seed = _input.get("seed")

    expected = _as_dict(_input.get("expected"))
    output = _as_dict(_input.get("output"))
    raw_answer = str(_input.get("raw_answer") or "")

    stop_reason = str(_input.get("stop_reason") or "")

    setup_success = _input.get("setup_success")
    setup_ok = bool(setup_success) if isinstance(setup_success, bool) else True

    issues = []

    if stop_reason == "max_iterations":
        issues.append("max_iterations_reached")

    exp_case = str(expected.get("case_id") or "")
    out_case = str(output.get("case_id") or "")
    if setup_ok and exp_case and out_case and exp_case != out_case:
        issues.append("case_id_mismatch")

    exp_tot = _as_dict(expected.get("totals"))
    out_tot = _as_dict(output.get("totals"))

    for k in ("orders_amount", "refunds_amount", "net_amount"):
        ev = _as_int(exp_tot.get(k))
        ov = _as_int(out_tot.get(k))
        if setup_ok and ev is not None and ov is not None and ev != ov:
            issues.append("totals_" + k + "_mismatch")

    exp_top = _as_list(expected.get("top_customers"))
    out_top = _as_list(output.get("top_customers"))

    if setup_ok and len(exp_top) != 3:
        issues.append("expected_top_customers_not_3")
    if setup_ok and len(out_top) != 3:
        issues.append("output_top_customers_not_3")

    def _normalize_top(lst):
        out = []
        for row in _as_list(lst):
            if not isinstance(row, dict):
                continue
            out.append(
                {
                    "customer_id": str(row.get("customer_id") or ""),
                    "name": str(row.get("name") or ""),
                    "net_amount": _as_int(row.get("net_amount")),
                }
            )
        return out

    exp_norm = _normalize_top(exp_top)
    out_norm = _normalize_top(out_top)

    if setup_ok and len(exp_norm) == 3 and len(out_norm) == 3:
        for i in range(3):
            if exp_norm[i].get("customer_id") != out_norm[i].get("customer_id"):
                issues.append("top_customer_id_mismatch_" + str(i))
            if exp_norm[i].get("net_amount") != out_norm[i].get("net_amount"):
                issues.append("top_net_amount_mismatch_" + str(i))

    correct = setup_ok and (len(issues) == 0)

    sig_parts = []
    if isinstance(out_case, str) and out_case:
        sig_parts.append("case_id=" + out_case)
    if len(out_norm) == 3:
        for r in out_norm:
            sig_parts.append(str(r.get("customer_id") or "") + ":" + str(r.get("net_amount")))
    if isinstance(out_tot, dict):
        oa = _as_int(out_tot.get("orders_amount"))
        ra = _as_int(out_tot.get("refunds_amount"))
        na = _as_int(out_tot.get("net_amount"))
        sig_parts.append("totals=" + str(oa) + "," + str(ra) + "," + str(na))
    signature = "|".join(sig_parts)

    steps = _as_list(_input.get("steps"))

    llm_calls = len(steps)
    tool_batches = 0
    tool_calls = 0
    max_tools_per_batch = 0
    multi_tool_batches = 0
    tool_errors = 0
    tools_used = {}

    steps_summary = []

    for s_any in steps:
        s = s_any if isinstance(s_any, dict) else None
        if s is None:
            continue
        idx = _as_int(s.get("index"))
        calls_raw = s.get("tool_calls")
        calls = []
        if isinstance(calls_raw, list):
            for c in calls_raw:
                if isinstance(c, dict):
                    calls.append(c)
        elif isinstance(calls_raw, dict):
            calls = [calls_raw]

        n_calls = len(calls)
        if n_calls > 0:
            tool_batches += 1
            tool_calls += n_calls
            if n_calls > max_tools_per_batch:
                max_tools_per_batch = n_calls
            if n_calls >= 2:
                multi_tool_batches += 1
            for c in calls:
                name = c.get("name")
                if isinstance(name, str) and name:
                    tools_used[name] = int(tools_used.get(name, 0) or 0) + 1

        results_raw = s.get("tool_results")
        if isinstance(results_raw, list):
            for r_any in results_raw:
                r = r_any if isinstance(r_any, dict) else None
                if r is None:
                    continue
                if r.get("success") is not True:
                    tool_errors += 1

        tool_names = []
        for c in calls:
            name = c.get("name")
            if isinstance(name, str) and name:
                tool_names.append(name)

        steps_summary.append({"index": idx, "n_tool_calls": int(n_calls), "tool_names": tool_names})

    metrics = {
        "llm_calls": int(llm_calls),
        "tool_batches": int(tool_batches),
        "tool_calls": int(tool_calls),
        "max_tools_per_batch": int(max_tools_per_batch),
        "multi_tool_batches": int(multi_tool_batches),
        "tool_errors": int(tool_errors),
        "tools_used": tools_used,
        "stop_reason": stop_reason,
    }

    record = {
        "mode": mode,
        "prompt_id": prompt_id,
        "prompt_label": prompt_label,
        "repeat": int(repeat_i),
        "case_dir": case_dir,
        "case_id": case_id,
        "index_path": index_path,
        "config": {
            "provider": provider,
            "model": model,
            "temperature": temperature,
            "seed": seed,
        },
        "setup": {
            "success": bool(setup_ok),
            "results": _as_list(_input.get("setup_results")),
        },
        "output": output,
        "expected": expected,
        "correct": bool(correct),
        "issues": issues,
        "signature": signature,
        "metrics": metrics,
        "debug": {
            "raw_answer": raw_answer,
            "steps_summary": steps_summary,
        },
    }

    return record
